import './App.css';
import axios from 'axios';

export class App extends Component {

state={
      person:[]
}



componentDidMount(){
  axios.get(`https://jsonplaceholder.typicode.com/posts`)
  .then(res => {
     console.log('res');
     this.setState({person:res.data});
  })
  // .catch(error => {
  //   console.log('Error fetching and parsing data', error);
  // });
}
  render() {
    return ( 
      <div>
      <p>Checking react lifecycle.....</p>

      <div className="App">
      <header className="App-header">
      <ul>{this.state.person.map(person=><li>{person.name}</li>)}</ul>


        </header>
      </div>
      </div>
    )
  }
}

export default App
